mapper.select:
SELECT UID,UserName, PWD, RealName, Question, Answer,LinkedTID
from User
WHERE UserName = #{UserName}

SELECT UID,UserName, PWD, RealName, Question, Answer, LinkedTID
from User
WHERE UID = #{UID}

mapper.update:
UPDATE User
SET PWD = #{PWD}
WHERE UserName = #{UserName}

mapper.select:
SELECT TID, TeamName, Auth, Leader, Objective
from Team
WHERE TID = #{TID}

SELECT TID, TeamName, Auth, Leader, Objective
FROM Team
WHERE Leader = #{Leader}

mapper.update:
UPDATE User
SET LinkedTID = #{LinkedTID}
WHERE UID = #{UID}
